.downMenu{
    width:50px;
    height:110px;
    margin-right: 20px;
    margin-left: -40px;
    justify-content: flex-end;
}
.downMenu > .downMenu_1{
    display:block;
    width:35px;
    height:35px;
}
.downMenu > .downMenu_2{
    margin-top:100px;
    justify-content: space-between;
    display:none;
}
.downMenu > .downMenu_2 > ul{
    margin-bottom: -20px;
    display:block;
}
.downMenu > .downMenu_2 > ul > li{
    /*flex-direction: column;*/
    width:30px;
    height:30px;
    margin-bottom:20px;
    display: block;
}
/*鼠标移入，展现选项，同时增大区域长宽度防止消失；*/
.downMenu:hover{
    height:400px;
}
.downMenu:hover > .downMenu_2{
    display:block;
    backdrop-filter:saturate(150%) blur(4px);
    -webkit-backdrop-filter:saturate(150%) blur(4px);
    background-color:rgba(28,13,26,0.3);
}

.globalAside{
    min-width: 340px;
    margin: 35px 120px 0 120px;
}
.globalAside > .Aside{
    color:white;
    margin-top:20px;
    height:212px;
    backdrop-filter:saturate(150%) blur(4px);
    -webkit-backdrop-filter:saturate(150%) blur(4px);
    background-color:rgba(28,13,26,.2);
}

