    font-size: 20px;
    margin-top: 15px;
    margin-left: 85px;
}
.globalAside .time_Weather .line {
    margin-top: 10px;
    height:1px;
    background: #80808080;
}
.globalAside > .container_menu > .menu button{
    width:30px;
