.globalRight{
    /*outline:1px solid red;*/
    min-width: 1000px;
    margin: 35px 120px 0 auto;
}

.globalRight > .globalHeader{
    margin-top:40px;
    margin-bottom:50px;
}
.globalRight > .globalHeader > .searchForm{
        display: flex;
        justify-content: space-between;
}
.globalRight > .globalHeader >.searchForm > input {
    width: 100%;
    margin-right: -3px;
    height: 40px;
    padding: 0 10px;
    border: none;
    outline: none;
    border-radius: 5px;
}
.globalRight > .globalHeader >.searchForm > button {
    border: none;
    white-space: nowrap;
    padding: 0 26px;
    background: #2090CF;
    color: white;
    font-size: 16px;
}

.globalRight > .globalSort{
    display:flex;
    justify-content: space-between;
    margin:30px 0;
    background: #eeeeee80;
}

.globalRight > .globalSort > ul{
    width: 100%;
    display:flex;
    margin:10px 25px;
    justify-content: space-between;
}
.globalRight > .globalSort > ul > li{
    background: #ffffffF2;
    padding: 10px 33px;
    display: block;
    width: 100px;
    height: 41px;
    font-size: 16px;
}
.globalRight > .globalSort > ul > li:hover{
    background: #2090CF;
    color:white;
}
.globalRight > .globalSort > .killWall{
    border:1px solid #22222280;
    width: 27px;
    height: 25px;
    border-radius: 50%;
    margin-right: 15px;
    margin-top: 19px;
    background: rgb(0,128,0);
    background: radial-gradient(circle, rgba(0,128,0,1) 0%, rgba(0,128,0,1) 36%, rgba(255,255,255,1) 36%, rgba(255,255,255,1) 100%);
}

.globalRight > .globalMain{
    display: flex;
    margin-left: 7px;
    margin-right: 7px;
}
.globalRight > .globalMain > .siteList {
    display: flex;
    flex-wrap: wrap;
    margin-right: -36px;
}
.globalRight > .globalMain > .siteList .site {
    /*border:1px solid pink;*/
    width: 110px;
    height: 100px;
    background: #ffffff80;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    margin-bottom: 20px;
    margin-right: 36px;
    border-radius: 4px;
    padding: 15px 0;
    position: relative;
    /*!*鼠标移上变手形*!*/
    cursor:pointer;
}

.globalRight > .globalMain > .siteList .site:hover {
    background: #ffffff;
}

.globalRight > .globalMain > .siteList .site .logo {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
}
.globalRight > .globalMain > .siteList .site .link {
    font-size: 14px;
    margin-top: 4px;
}
.globalRight > .globalMain > .siteList .site .close{
    position:absolute;
    right:1px;
    top:-10px;
    font-size: 30px;
    cursor:default;
    display: none;
}
.globalRight > .globalMain > .siteList .site:hover .close{
    display:block;
}

.globalRight > .globalMain > .siteList .addSite .icon {
    width:64px;
    height:64px;
}
.globalRight > .globalMain > .siteList .addSite .text{
    font-size: 16px;
    margin-top: 4px;
}
